create sequence sequence_generator MINVALUE 1 MAXVALUE 999999999999999999  START WITH 1 INCREMENT BY 1  NO CYCLE;
CREATE TABLE alert (id bigint PRIMARY KEY NOT NULL,   guid varchar(255),   name varchar(255),   severity varchar(255),   monitorcondition varchar(255),   affectedresource varchar(255),   monitorservice varchar(255),   signaltype varchar(255),   brcsubscription varchar(255),   suppressionstate varchar(255),   resourcegroup varchar(255),   resources varchar(255),   firedtime varchar(255),   created_on timestamp,   updated_on timestamp,   alert_state varchar(255),   client varchar(255),   client_url varchar(255),   description varchar(5000),   details varchar(255),   incident_key varchar(255));
CREATE TABLE alert_activity (id bigint PRIMARY KEY NOT NULL,   guid varchar(255),   name varchar(255),   action varchar(255),   action_description varchar(5000),   created_on timestamp,   updated_on timestamp,   alert_state varchar(255),   ticket_id bigint,   ticket_name varchar(255),   ticket_url varchar(500),   ticket_description varchar(5000),   user_name varchar(255),   event_type varchar(255),   change_log text,   fired_time timestamp);
